(151, 1, '2021-10-03 14:55:17', '2021-10-03 11:55:17', '<!-- wp:heading {\"textAlign\":\"center\",\"align\":\"full\",\"style\":{\"typography\":{\"fontSize\":\"46px\"}},\"className\":\"space_header\"} -->\n<h2 class=\"alignfull has-text-align-center space_header\" style=\"font-size:46px\">Космические путешествия</h2>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"textAlign\":\"center\",\"className\":\"animate__animated animate__bounce\"} -->\n<h2 class=\"has-text-align-center animate__animated animate__bounce\">Наши преимущества</h2>\n<!-- /wp:heading -->\n\n<!-- wp:shortcode -->\n[contact-form-7 id=\"122\" title=\"Contact form 1\"]\n<!-- /wp:shortcode -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3>Наш лозунг: Возможности без границ</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"fontSize\":\"large\"} -->\n<p class=\"has-large-font-size\"><strong>И еще:</strong> Легко!</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"textAlign\":\"center\"} -->\n<h2 class=\"has-text-align-center\" id=\"servises\">Наши услуги</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">Что мы предлагаем</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:columns {\"align\":\"full\"} -->\n<div class=\"wp-block-columns alignfull\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:paragraph {\"align\":\"center\",\"style\":{\"color\":{\"text\":\"#5bc0de\"}},\"fontSize\":\"extra-large\"} -->\n<p class=\"has-text-align-center has-text-color has-extra-large-font-size\" style=\"color:#5bc0de\">[icon name=\"rocket\"]</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"textAlign\":\"center\",\"level\":4} -->\n<h4 class=\"has-text-align-center\">Минимальное время</h4>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\"> cтремимся к скорости света!</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:paragraph {\"align\":\"center\",\"style\":{\"color\":{\"text\":\"#5bc0de\"}},\"fontSize\":\"extra-large\"} -->\n<p class=\"has-text-align-center has-text-color has-extra-large-font-size\" style=\"color:#5bc0de\">[icon name=\"graduation-cap\"]</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"textAlign\":\"center\",\"level\":4} -->\n<h4 class=\"has-text-align-center\">Обучение</h4>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">научим инопланетным тонкостям</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:paragraph {\"align\":\"center\",\"style\":{\"color\":{\"text\":\"#5bc0de\"}},\"fontSize\":\"extra-large\"} -->\n<p class=\"has-text-align-center has-text-color has-extra-large-font-size\" style=\"color:#5bc0de\"> [icon name=\"lock\"] </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"textAlign\":\"center\",\"level\":4} -->\n<h4 class=\"has-text-align-center\">Безопасность</h4>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">ваш корабль — корабль-сейф!</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:heading {\"textAlign\":\"center\"} -->\n<h2 class=\"has-text-align-center\" id=\"portfolio\">Портфолио</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">Где мы работаем:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:columns {\"align\":\"full\",\"className\":\"portfolio_planets\"} -->\n<div class=\"wp-block-columns alignfull portfolio_planets\"><!-- wp:column {\"style\":{\"spacing\":{\"padding\":{\"top\":\"20px\",\"right\":\"20px\",\"bottom\":\"20px\",\"left\":\"20px\"}}}} -->\n<div class=\"wp-block-column\" style=\"padding-top:20px;padding-right:20px;padding-bottom:20px;padding-left:20px\"><!-- wp:image {\"id\":104,\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-full\"><img src=\"http://localhost/wp21/wp-content/uploads/2021/09/mercury.jpg\" alt=\"\" class=\"wp-image-104\"/><figcaption><strong>Меркурий</strong><br>Наша миссия на Меркурии</figcaption></figure>\n<!-- /wp:image --></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"style\":{\"spacing\":{\"padding\":{\"top\":\"20px\",\"right\":\"20px\",\"bottom\":\"20px\",\"left\":\"20px\"}}}} -->\n<div class=\"wp-block-column\" style=\"padding-top:20px;padding-right:20px;padding-bottom:20px;padding-left:20px\"><!-- wp:image {\"id\":105,\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-full\"><img src=\"http://localhost/wp21/wp-content/uploads/2021/09/earth.jpg\" alt=\"\" class=\"wp-image-105\"/><figcaption><strong>Земля</strong><br>Наша миссия на Земле</figcaption></figure>\n<!-- /wp:image --></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"style\":{\"spacing\":{\"padding\":{\"top\":\"20px\",\"right\":\"20px\",\"bottom\":\"20px\",\"left\":\"20px\"}}}} -->\n<div class=\"wp-block-column\" style=\"padding-top:20px;padding-right:20px;padding-bottom:20px;padding-left:20px\"><!-- wp:image {\"id\":106,\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-full\"><img src=\"http://localhost/wp21/wp-content/uploads/2021/09/venus.jpg\" alt=\"\" class=\"wp-image-106\"/><figcaption><strong>Венера</strong><br>Наша миссия на Венере</figcaption></figure>\n<!-- /wp:image --></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"style\":{\"spacing\":{\"padding\":{\"top\":\"20px\",\"right\":\"20px\",\"bottom\":\"20px\",\"left\":\"20px\"}}}} -->\n<div class=\"wp-block-column\" style=\"padding-top:20px;padding-right:20px;padding-bottom:20px;padding-left:20px\"><!-- wp:image {\"id\":107,\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-full\"><img src=\"http://localhost/wp21/wp-content/uploads/2021/09/mars.jpg\" alt=\"\" class=\"wp-image-107\"/><figcaption> <strong>Марс</strong><br>Наша миссия на Марсе </f